function setupCarousels(){document.querySelectorAll("[data-carousel]").forEach(t=>{let r=t.querySelector("[data-rail]");var a=t.querySelector("[data-prev]"),t=t.querySelector("[data-next]");let l=260;if(r){let e=e=>{r.scrollBy({left:e*l,behavior:"smooth"})};a?.addEventListener("click",()=>e(-1)),t?.addEventListener("click",()=>e(1))}})}function init(){setupCarousels()}document.addEventListener("DOMContentLoaded",init);